import React, { useEffect, useState } from "react";
import Home from "./views/Home/Home";
import NavBar from "./components/NavBar/NavBar";
import Portfolio from "./views/Portfolio/Portfolio";
import About from "./views/About/About";
import Contact from "./views/Contact/Contact";

function App() {
  const [isNavTransparent, setIsNavTransparent] = useState(true);

  useEffect(() => {
    const handleScroll = () => {
      // Comprueba la posici贸n del scroll para determinar la transparencia de la barra de navegaci贸n
      if (window.scrollY > 100) {
        setIsNavTransparent(false);
      } else {
        setIsNavTransparent(true);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="App">
      <NavBar isTransparent={isNavTransparent} />
      <div id="home">
        <Home />
      </div>
      <div id="about">
        <About />
      </div>
      <div id="portfolio">
        <Portfolio />
      </div>
      <div id="contact">
        <Contact />
      </div>
    </div>
  );
}

export default App;
