import React, { useState } from "react";
import Carousel from "../Carrusel/Carrusel";
import "./Cards.module.css"; // Importa tu archivo CSS aquí

const Cards = () => {
  const [showDetail, setShowDetail] = useState(false);

  const toggleDetail = () => {
    setShowDetail(!showDetail);
  };

  const imageStyle = {
    maxWidth: "100px", // Ajusta el tamaño máximo de las imágenes
    height: "auto", // Mantiene la proporción de aspecto de la imagen
    marginRight: "10px", // Agrega margen derecho para separar las imágenes
  };

  return (
    <div className="card-container">
      <img
        src="/imagenpi.png"
        alt="Proyecto Individual"
        className="card-image"
        style={imageStyle}
        onClick={toggleDetail}
      />
      <img
        src="/imagenpf.png"
        alt="Proyecto Grupal"
        className="card-image"
        style={imageStyle}
        onClick={toggleDetail}
      />

      {showDetail && <Carousel className="carousel" />}
    </div>
  );
};

export default Cards;
