import React from "react";
import { Link } from "react-router-dom";
import "./Portfolio.module.css"; // Asegúrate de que la importación del archivo CSS sea correcta

const Portfolio = () => {
  const projects = [
    {
      id: 1,
      title: "Proyecto 1",
      description: "Descripción del proyecto 1...",
      image: "imagenpf.png",
    },
    {
      id: 2,
      title: "Proyecto 2",
      description: "Descripción del proyecto 2...",
      image: "imagenpi.png",
    },
    // Agrega más proyectos aquí
  ];

  return (
    <div className="portfolio-container">
      <h1>Portfolio.</h1>
      <p>Consulte algunos de mis últimos proyectos</p>
      <pre>
        He estado trabajando e innovando constamentemente, incorporando nuevos
        conocimientos y
        <pre>
        tecnologías, para de ésta manera seguir creciendo en mi carrera
        </pre>
      </pre>

      <div className="projects-list">
        {projects.map((project) => (
          <div className="project" key={project.id}>
            <img
              src={project.image}
              alt={project.title}
              style={{ maxWidth: "100%", height: "auto", maxHeight: "250px" }}
            />
            <h2>{project.title}</h2>
            <p>{project.description}</p>
            
          </div>
        ))}
      </div>
    </div>
  );
};

export default Portfolio;
