import React, { useEffect, useState } from "react";
import style from "./NavBar.module.css";
import SocialLinks from "../../views/Contact/Contact";
import Logo from "../Logo/Logo";

const NavBar = () => {
  const [isNavFixed, setIsNavFixed] = useState(false);

  useEffect(() => {
    // Funci贸n para controlar la posici贸n del scroll
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsNavFixed(true);
      } else {
        setIsNavFixed(false);
      }
    };

    // Agrega un listener para el evento de scroll
    window.addEventListener("scroll", handleScroll);

    // Limpia el listener cuando el componente se desmonta
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const navClass = isNavFixed ? `${style.mainContainer} ${style.fixedNav}` : style.mainContainer;

  return (
    <div className={navClass}>
      <Logo className={style.logo} />
      <a href="#about">ABOUT ME</a>
      <a href="#portfolio">PORTFOLIO</a>
      <a href="#contact">CONTACT</a>
      <SocialLinks className={style.socialLinks} />
    </div>
  );
};

export default NavBar;
