import React, { useState } from "react";
import Carousel from "../Carrusel/Carrusel";
import "./Cards.module.css"; 

const Cards = () => {
  const [showDetail, setShowDetail] = useState(false);

  const toggleDetail = () => {
    setShowDetail(!showDetail);
  };

  const imageStyle = {
    maxWidth: "650px", 
    height: "auto", 
    marginLeft: "25px",
    marginRight: "25px",
  };

  const proyectos = [
    {
      id: 1,
      nombre: "Proyecto Individual 1",
      imagenes: [
        "/imagenpi1.png",
        "/imagenpi2.png",
        "/imagenpi3.png",
      ],
    },
    {
      id: 2,
      nombre: "Proyecto Grupal 1",
      imagenes: [
        "/pf2.png",
        "/pf3.png",
        "/pf4.png",
        "/pf5.png",
      ],
    },
  ];
};

export default Cards;
