import React from "react";
import { useForm, Controller } from "react-hook-form";
import "./Form.module.css"; 

const ContactForm = () => {
  const { handleSubmit, control, reset } = useForm();

  const onSubmit = (data) => {
    console.log("Datos del formulario:", data);
    reset();
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Contáctame</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="form-group">
          <label className="form-label" htmlFor="name">
            Nombre:
          </label>
          <Controller
            name="name"
            control={control}
            defaultValue=""
            render={({ field }) => <input className="form-input" {...field} type="text" />}
          />
        </div>
        <div className="form-group">
          <label className="form-label" htmlFor="email">
            Correo Electrónico:
          </label>
          <Controller
            name="email"
            control={control}
            defaultValue=""
            render={({ field }) => <input className="form-input" {...field} type="email" />}
          />
        </div>
        <div className="form-group">
          <label className="form-label" htmlFor="message">
            Mensaje:
          </label>
          <Controller
            name="message"
            control={control}
            defaultValue=""
            render={({ field }) => <textarea className="form-input" {...field} rows="4" />}
          />
        </div>
        <button className="submit-button" type="submit">
          Enviar
        </button>
      </form>
    </div>
  );
};

export default ContactForm;
