import React, { useState } from "react";
import Detail from "../Detail/Detail";
import "./Portfolio.module.css"; // Importa el archivo CSS aquÃ­

const projectsData = [
  {
    id: 1,
    title: "Final Project",
    subtitle: "Vinyls-Trade",
    description: "E-Commerce / Marketplace",
    images: ["imagenpf.png", "pf2.png", "pf3.png", "pf4.png", "pf5.png"],
  },
  {
    id: 2,
    title: "Individual Project",
    subtitle: "Food",
    description: "Food Recipe Page",
    images: ["imagenpi.png", "pi2.png", "pi3.png", "pi4.png"],
  },
];

const Portfolio = () => {
  const [selectedProject, setSelectedProject] = useState(null);

  const toggleExpand = (projectId) => {
    setSelectedProject((prevSelectedProject) =>
      prevSelectedProject === projectId ? null : projectId
    );
  };

  return (
    <div className={style.texto}>
      <h1 className="">Proyectos</h1>
      <p>Check out some of my recent work</p>
      <p>
        I've been working day by day to improve my coding practices and thus,
        achieve excellence to kickstart my tech career! ðŸš€
      </p>
      <hr />

      <div className="projects-list">
        {projectsData.map((project) => (
          <div className="project" key={project.id}>
            <div
              className={`project-card ${
                selectedProject === project.id ? "expanded" : ""
              }`}
              onClick={() => toggleExpand(project.id)}
            >
              <img
                src={project.images[0]}
                alt={project.title}
                className="project-image"
              />
              <h2 className="project-title">{project.title}</h2>
              <p className="project-subtitle">{project.subtitle}</p>
              <pre className="project-description">{project.description}</pre>
            </div>
          </div>
        ))}
      </div>
      {selectedProject && (
        <Detail
          project={projectsData.find((project) => project.id === selectedProject)}
          onClose={() => setSelectedProject(null)}
        />
      )}
    </div>
  );
};

export default Portfolio;
