import "./App.css";
import { Route, Switch } from "react-router-dom";
import Home from "./views/Home/Home";
import NavBar from "./components/NavBar/NavBar";
import Cards from "./components/Cards/Cards";
import About from "./views/About/About";
import Contact from "./views/Contact/Contact";
import ContactForm from "./components/Form/Form";

function App() {
  return (
    <div className="App">
      {/* NavBar siempre se renderiza */}
      <Route path="/" component={NavBar} />

      <Switch>
        {/* Home se renderiza solo en la ruta "/" */}
        <Route exact path="/" component={Home} />
      </Switch>

      {/* El contenido de /home se renderiza solo en la ruta "/" */}
      {window.location.pathname === "/" && (
        <div>
          <section id="about"><h2>ABOUT</h2>

          </section>
          <section id="portfolio"><h2>PORTFOLIO</h2>

          </section>
          <section id="contact"><h2>CONTACT</h2>
            <ContactForm/>

          </section>
        </div>
      )}
    </div>
  );
}

export default App;
