import React, { useState } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";

const Proyectos = () => {
  const [proyectoSeleccionado, setProyectoSeleccionado] = useState(null);

  const handleProyectoClick = (proyecto) => {
    setProyectoSeleccionado(proyecto);
  };

  const imageStyle = {
    maxWidth: "150px", // Ajusta el tamaño máximo de las imágenes
    height: "auto", // Mantiene la proporción de aspecto de la imagen
  };

  return (
    <div>
      {proyectos.map((proyecto) => (
        <div key={proyecto.id} onClick={() => handleProyectoClick(proyecto)}>
          <img
            src={proyecto.imagenes[0]}
            alt={proyecto.nombre}
            style={imageStyle}
          />
        </div>
      ))}

      {proyectoSeleccionado && (
        <div>
          <h2>{proyectoSeleccionado.nombre}</h2>
          <Carousel autoPlay={true} interval={3000} showStatus={false} showThumbs={false}>
            {proyectoSeleccionado.imagenes.map((imagen, index) => (
              <div key={index}>
                <img src={imagen} alt={`Imagen ${index + 1}`} style={imageStyle} />
              </div>
            ))}
          </Carousel>
        </div>
      )}
    </div>
  );
};

export default Proyectos;
