import React from "react";
import style from "./Home.module.css";
import { Link } from "react-router-dom";

const Home = () => {
  const curriculumURL = "file:///C:/Users/Planti/Desktop/Curriculums/CV_SantiagoG%C3%B3mezTartaglino.pdf"

  const downloadCurriculum = () => {
    const link = document.createElement("a");
    link.href = curriculumURL;
    link.download = "TuNombre_CV.pdf"; // Puedes personalizar el nombre del archivo descargado
    link.click();
  };

  return (
    <div className={style.container}>
      <h1 className={style.inicio1}>Santiago Gómez Tartaglino</h1>
      <h1 className={style.inicio2}>Fullstack Developer </h1>
      <h1 className={style.inicio3}>enfocado en la</h1>
      <h1 className={style.innovacion}>constante innovación</h1>
      <div className={style.buttonContainer}>
        <button className={style.cv} onClick={downloadCurriculum}>
          <h2>Descargar CV</h2>
        </button>
      </div>
    </div>
  );
};

export default Home;
